mapper.update:
UPDATE m_account
SET modify_date =now(),
mobile=#{mobile}, nation_code=#{nationCode}
WHERE id = #{accountId}

UPDATE m_account
SET modify_date =
now(),password=#{newPassword}
WHERE
id
= #{accountId}

	and password = #{oldPassword}

UPDATE m_account SET locked =#{locked}
,last_pay_date=NOW()
WHERE id=#{accountId}

UPDATE m_account
SET modify_date = now(),
red_amount=red_amount+#{redAmount}

	, red_date=#{redDate}

WHERE id = #{accountId} AND (red_amount+#{redAmount})>=0

UPDATE m_account
SET modify_date = now(),
limit_gift_amount=limit_gift_amount+#{limitGiftAmount}

	, limit_date=#{limitDate}

WHERE id = #{accountId}

UPDATE m_account
SET status=#{status}, modify_date=now()
WHERE id=#{id}
