mapper.select:
select * from (SELECT settle_year as year,settle_month as month,designer_id,designer_code,designer_name, sum(quantity) as quantity,sum(tag_price*quantity) as tag_amount, SUM(settle_amount) as settle_amount FROM o_statement_item where settle_year=#{settleYear} and settle_month=#{settleMonth} and status=0 and statement_id is null and settle_price is not null and NOT EXISTS (SELECT 1 FROM o_statement_item s WHERE s.settle_year=#{settleYear} and s.settle_month= #{settleMonth} and s.settle_amount IS NULL AND s.designer_id=o_statement_item.designer_id)
and designer_id=#{designerId}
group by designer_id
)a where designer_id is not null

select count(id) as count,designer_id as designerId from o_statement
where year=#{settleYear}
and
month=#{settleMonth}

	and designer_id=#{designerId}

group by designer_id

select * from o_statement where 1=1



order by create_date desc

	limit #{page.startNumber},#{page.pageSize}

mapper.update:
update o_statement set
remark=#{memo},modify_date=now()
where id=#{id}

update o_statement set status=0
,designer_memo=#{memo}
where id=#{id} and status=2

update o_statement set
status=8,last_modify_man=#{operator}
,modify_date=now(),finish_date=#{finishDate} where
id=#{id}
and status=3

update o_statement set
payer=#{payer},pay_sn=#{paySn},pay_date=#{payDate},last_modify_man=#{operator}
,modify_date=now(),pay_bank=#{payBank},pay_pic=#{payPic},pay_money=#{payMoney},pay_memo=#{payMemo}
where id=#{id}
and status=3

update o_statement set
compensation_count=#{compensationCount},
compensation_amount=#{compensationAmount},actual_compensation_amount=#{actualCompensationAmount}
where id=#{id}
